def print_general(message, duration, size, wraplen, font_n, col):
	string_output = ''
	for char in message: 
		string_output += char 
		OUTPUT_BOX.config(text = string_output, font = f'{font_n} {size}',
			wraplength = wraplen, justify = LEFT, fg = col, pady = 2) 

		waithere(duration)

